import { useState, useEffect, useMemo } from "react";

const superSlowFunction = (num) => {
  // console.log("Super Slow Function Is Running ü¶•");
  let count = 0;
  //NOTE Delay the return
  while (count <= 1000000000) {
    count++;
  }
  return num * 2;
};

const UseMemoExample = () => {
  const [number, setNumber] = useState(0);
  const [colorChange, setColorChange] = useState(false);

  const doubleNumber = useMemo(() => superSlowFunction(number), [number]);

  //referential equality
  const appStyle = useMemo(() => {
    return {
      backgroundColor: colorChange ? "tomato" : "white",
    };
  }, [colorChange]);

  useEffect(() => {
    // console.log("Background Changed üåà"); //NOTE Uncomment when testing
  }, [appStyle]);

  return (
    <div className="mx-auto mt-5 d-grid gap-3 w-50" style={appStyle}>
      <h5 className="alert alert-primary text-center">ÿ¢ÿ¥ŸÜÿß€å€å ÿ®ÿß ŸáŸà⁄© useMemo</h5>
      <input
        type="number"
        className="form-control"
        value={number}
        onChange={(e) => setNumber(e.target.value)}
        placeholder="ÿπÿØÿØ Ÿàÿßÿ±ÿØ ÿ®ŸÜŸÖÿß"
      />
      <button
        type="button"
        className="btn btn-info btn-block"
        onClick={() => setColorChange((prevColorChange) => !prevColorChange)}
      >
        ÿ±ŸÜ⁄Ø ÿ±Ÿà ÿ™ÿ∫€å€åÿ± ÿ®ÿØŸá üñåÔ∏è
      </button>
      <div style={appStyle} className="text-center mx-auto">
        <p className="alert alert-warning">{`ÿπÿØÿØ ÿØŸà ÿ®ÿ±ÿßÿ®ÿ± ÿ¥ÿØŸá ÿ®ÿ±ÿßÿ®ÿ± ÿßÿ≥ÿ™ ÿ®ÿß : ${doubleNumber}`}</p>
      </div>
    </div>
  );
};

export default UseMemoExample;
