import { useReducer, useState } from "react";

{/*
  comment
*/}

const reducer = (state , action) => {
  switch (action.type) {
    case "ICREAMENT":
      return {}
      break;

    default:
      break;
  }
}

const UseReducerExample = () => {

  const [count, setCount] = useState(1);

  return (
    <div className="mx-auto mt-5 d-grid gap-3 w-50">
      <h5 className="alert alert-info text-center">آشنایی با هوک useReducer</h5>
      <button className="btn btn-primary" onClick={() => setCount((prevCount) => (prevCount + 1))}>
        اضافه بنما 😉
      </button>
      <p className="alert alert-warning text-center">
        شمارش شما دوست عزیز برابر است با :{" "}
        <span className="badge rounded-pill bg-success">{count}</span>
      </p>
      <button className="btn btn-danger" onClick={() => setCount((prevCount) => prevCount - 1)}>
        کم بنما 🤠
      </button>
    </div>
  );
};

export default UseReducerExample;
